import React from "react";

function YourBotArmy({ bots }) {
  const {id, name, health, damage, armor, bot_class, catchphrase} = bots
  return (
    <div className="ui segment inverted olive bot-army">
      <div className="ui five column grid">
        <div className="row bot-army-row">
          <h2>Your Bot Army</h2>
          {bots.map((bot) => (
            <div className="column" key={id}>
              <img
                alt="oh no!"
                className="ui small circular image bordered"
                src={bot.avatar_url}
              />
              <div>
                <strong>Name:</strong> {name}
              </div>
              <div>
                <strong>Class:</strong> {bot_class}
              </div>
              <div>
                <strong>Health:</strong> {health}
              </div>
              <div>
                <strong>Damage:</strong> {damage}
              </div>
              <div>
                <strong>Armor:</strong> {armor}
              </div>
              <div>
                <strong>Catchphrase:</strong> {catchphrase}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default YourBotArmy;
