import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const botTypeClasses = {
  Assault: "icon military",
  Defender: "icon shield",
  Support: "icon plus circle",
  Medic: "icon ambulance",
  Witch: "icon magic",
  Captain: "icon star",
};

function BotSpecs({addToArmy}) {
  const navigate = useNavigate();
  const { id } = useParams();
  const [bot, setBot] = useState(null);

  useEffect(() => {
    // Fetch bot data based on the ID from the URL
    fetch(`http://localhost:8002/bots/${id}`)
      .then((response) => response.json())
      .then((data) => setBot(data))
      .catch((error) => console.error("Error fetching bot:", error));
  }, [id]);

  const handleGoBack = () => {
    // Navigate back to the previous page
    navigate(-1);
  };

  const handleEnlist = () => {
    // Implement enlist functionality or navigate to another page
    addToArmy(bot);
  };

  if (!bot) {
    // Render loading state or error message while fetching bot data
    return <div>Loading...</div>;
  }

  return (
    <div className="ui segment">
      <div className="ui two column centered grid">
        <div className="row">
          <div className="four wide column">
            <img
              alt="oh no!"
              className="ui medium circular image bordered"
              src={bot.avatar_url}
            />
          </div>
          <div className="four wide column">
            <h2>Name: {bot.name}</h2>
            <p>
              <strong>Catchphrase: </strong>
              {bot.catchphrase}
            </p>
            <strong>
              Class: {bot.bot_class}
              <i className={botTypeClasses[bot.bot_class]} />
            </strong>
            <br />
            <div className="ui segment">
              <div className="ui three column centered grid">
                <div className="row">
                  <div className="column">
                    <i className="icon large circular red heartbeat" />
                    <strong>{bot.health}</strong>
                  </div>
                  <div className="column">
                    <i className="icon large circular yellow lightning" />
                    <strong>{bot.damage}</strong>
                  </div>
                  <div className="column">
                    <i className="icon large circular blue shield" />
                    <strong>{bot.armor}</strong>
                  </div>
                </div>
              </div>
            </div>
            <button className="ui button fluid" onClick={handleGoBack}>
              Go Back
            </button>
            <button className="ui button fluid" onClick={handleEnlist}>
              Enlist
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BotSpecs;
