import React from "react";
import { Link } from "react-router-dom"; // Import Link for navigation

const botTypeClasses = {
  Assault: "icon military",
  Defender: "icon shield",
  Support: "icon plus circle",
  Medic: "icon ambulance",
  Witch: "icon magic",
  Captain: "icon star",
};

function BotCard({ bot, addToArmy, removeFromCollection }) {
  const handleClick = () => {
    addToArmy(bot);
  };

  const handleRemove = () => {
    removeFromCollection(bot.id);
  };

  return (
    <div className="ui column">

      <Link to={`/bot/${bot.id}`} style={{ textDecoration: "none", color: "inherit" }}>
        <div className="ui card" key={bot.id} onClick={handleClick}>
          <div className="image">
            <img alt="oh no!" src={bot.avatar_url} />
          </div>
          <div className="content">
            <div className="header">
              {bot.name}
              <i className={botTypeClasses[bot.bot_class]} />
            </div>
            <div className="meta text-wrap">
              <small>{bot.catchphrase}</small>
            </div>
          </div>
          <div className="extra content">
            <span>
              <i className="icon heartbeat" />
              {bot.health}
            </span>
            <span>
              <i className="icon lightning" />
              {bot.damage}
            </span>
            <span>
              <i className="icon shield" />
              {bot.armor}
            </span>
            <span>
              <div className="ui center aligned segment basic">
                <button className="ui mini red button" onClick={handleRemove}>
                  x
                </button>
              </div>
            </span>
          </div>
        </div>
      </Link>
    </div>
  );
}

export default BotCard;
